def calculerBP(C):
    P = sum(C)
    BP = [[False]*(P+1) for k in range(len(C)+1)]
    BP[0][0] = True

    for k in range(len(C)):
        for e in range(0,P+1):
            if BP[k][e]:
                BP[k+1][e+C[k]] = True
                BP[k+1][abs(e-C[k])] = True
    return BP

